const menuBtn=document.querySelector(".menu__btn"),menuLink=document.querySelector(".menu");menuBtn.addEventListener("click",(()=>{menuBtn.classList.toggle("menu__btn--active"),menuLink.classList.toggle("menu--active")}));const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const n=e.getAttribute("href").substr(1);document.getElementById(n).scrollIntoView({behavior:"smooth",block:"start"})}));